import React, { useEffect } from 'react';
import { Container } from 'react-bootstrap';
import gsap from 'gsap';


const LandingPage = () => {
  useEffect(() => {
    const circles = document.querySelectorAll('.circle');

    // Check if circles are found
    if (circles.length === 0) {
      console.error('No circles found.');
      return;
    }

    gsap.to(circles, {
      duration: 2,
      opacity: 0,
      repeat: -1,
      yoyo: true,
      stagger: 0.5,
      ease: "power1.inOut"
    });

    // Set random initial positions within the .landing-page container
    circles.forEach(circle => {
      gsap.set(circle, {
        x: () => Math.random() * (window.innerWidth - 100), // Adjust for circle width
        y: () => Math.random() * (window.innerHeight - 100) // Adjust for circle height
      });
    });
  }, []);

  return (
    <>
      <Container fluid className='landing-page background'>
        <h1 className='Title'>Find Your Team,</h1>
        <h1 className='Title'>Join the Community.</h1>
        {/* Add circles to the page */}
        <div className="circle"></div>
        <div className="circle"></div>
        <div className="circle"></div>
      </Container>
    </>
  );
};

export default LandingPage;
