import React, { useEffect } from 'react';
import { Container } from 'react-bootstrap';
import gsap from 'gsap';


const LandingPage = () => {

    useEffect(() => {
        const circles = document.querySelectorAll('.circle');
        // Test animation with basic properties
        gsap.to(circles, {
            duration: 2,
            scale: 1.5, // Simple scale animation to check visibility
            repeat: -1,
            yoyo: true,
            stagger: 0.5,
            ease: "power1.inOut"
        });

        // Get the container element
        const container = document.querySelector('.container');

        // Get the container's bounding box
        const containerRect = container.getBoundingClientRect();

        // Calculate container dimensions and position
        const containerWidth = containerRect.width;
        const containerHeight = containerRect.height;
        const containerLeft = containerRect.left;
        const containerTop = containerRect.top;

        // Set the position of each circle relative to the container
        circles.forEach(circle => {
            gsap.set(circle, {
                x: () => Math.random() * (containerWidth - 100) + containerLeft, // Adjust for circle width
                y: () => Math.random() * (containerHeight - 100) + containerTop // Adjust for circle height
            });
        });


    }, []);



    return (
        <>
            <Container fluid className='landing-page background'>
                <h1 className='Title'>Find Your Team,</h1>
                <h1 className='Title'>Join the Community.</h1>
                {/* Add circles to the page */}
                <div className="circle"></div>
                <div className="circle"></div>
                <div className="circle"></div>
            </Container>
        </>
    );
};

export default LandingPage;
