import React, { useEffect } from 'react';
import { Container } from 'react-bootstrap';
import gsap from 'gsap';

const LandingPage = () => {

    useEffect(() => {
        const container = document.querySelector('.landing-page');
        const circles = document.querySelectorAll('.circle');

        // Get the container's dimensions and position
        const containerRect = container.getBoundingClientRect();
        console.log('Container dimensions:', containerRect);

        // Set the position of each circle relative to the container and log their coordinates
        circles.forEach(circle => {
            const circleSize = 100; // Size of the circle
            const xPosition = Math.random() * (containerRect.width - circleSize);
            const yPosition = Math.random() * (containerRect.height - circleSize);

            // Set the position using CSS left and top properties instead of transform properties
            gsap.set(circle, {
                left: xPosition,
                top: yPosition,
                opacity: 1 // Set opacity directly to 1
            });

            // Log the circle's position relative to the container
            console.log('Circle position:', { left: xPosition, top: yPosition });
        });

        // Test animation with basic properties
        gsap.to(circles, {
            duration: 2,
            scale: 1.5, // Simple scale animation to check visibility
            repeat: -1,
            yoyo: true,
            stagger: 0.5,
            ease: "power1.inOut"
        });
    }, []);

    return (
        <>
            <Container fluid className='landing-page background'>
                <h1 className='Title'>Find <span style={{color:"#963D5A"}}>Your</span> Team,</h1>
                <h1 className='Title'>Join the <span style={{color:'#963D5A'}}>Community.</span></h1>
                {/* Add circles to the page */}
                <div className="circle"></div>
                {/* <div className="circle"></div>
                <div className="circle"></div> */}
            </Container>
        </>
    );
};

export default LandingPage;
